import * as React from 'react';
import { Menu, MenuContent, MenuGroup, MenuList, MenuItem, Divider } from '@patternfly/react-core';
import "@patternfly/react-core/dist/styles/base.css";
import "./DormFloorList.css";
import {FC} from "react";
import {IFloor, ISwitch} from "../../../core/models";

interface ISwitchLink {
    floorId: string;
    doorId: string;
}
export const DormSwitch: FC<ISwitch & ISwitchLink> = ({ id, name, doorId, floorId }) => {
    return <MenuItem to={`/${doorId}/${floorId}/${id}`} itemId={id}>{ name }</MenuItem>
}

export const DormFloorList: FC<{ floors: IFloor[], doorId: string }> = ({ floors, doorId }) => {
    const [activeItem, setActiveItem] = React.useState<string | number | undefined>();
    const onSelect = (_event: React.MouseEvent<Element, MouseEvent> | undefined, itemId: string | number | undefined) => {
        console.log(`clicked ${itemId}`);
        setActiveItem(itemId);
    };
// Інтерактивні списки комутаторів із групуванням по поверхам
    return (
        <Menu className="dorm-list" onSelect={onSelect} activeItemId={activeItem}>
            <MenuContent>
                {/*група*/}
                {/*гарна полосочка*/}
                <Divider />
                {floors.map((floor, idx) => {
                    if (!floor?.switches) {
                        return <MenuGroup>
                            {/*список елементів групи*/}
                            <MenuList>
                                {/*елемент групи із посиланням на дію\сторінку\тощо*/}
                                <MenuItem to="#" itemId={0}>
                                    Central
                                </MenuItem>
                            </MenuList>
                        </MenuGroup>
                    }
                    return <MenuGroup label={floor.name} labelHeadingLevel="h3">
                        <MenuList>
                            {floor?.switches && floor?.switches.map(switchItem =>
                                <DormSwitch {...switchItem} floorId={floor.id} doorId={doorId} />)}
                        </MenuList>
                    </MenuGroup>
                    }
                )}
            </MenuContent>
        </Menu>
    );
};
