import * as React from 'react';
import { Menu, MenuContent, MenuGroup, MenuList, MenuItem, Divider } from '@patternfly/react-core';
import "@patternfly/react-core/dist/styles/base.css";
import "./DormFloorList.css";
import {FC} from "react";
import {IFloor, ISwitch} from "../../../core/models";

export const DormSwitch: FC<ISwitch> = ({ name }) => {
    return <MenuItem to="#" itemId={name}>{ name }</MenuItem>
}

export const DormFloorList: FC<{ floors: IFloor[] }> = ({ floors }) => {
    const [activeItem, setActiveItem] = React.useState(0);

    const onSelect = (_event: React.MouseEvent<Element, MouseEvent> | undefined, itemId: number | string | undefined) => {
        const item = itemId as number; // eslint-disable-next-line no-console
        console.log(`clicked ${item}`);
        setActiveItem(item);
    };
// Інтерактивні списки комутаторів із групуванням по поверхам
    return (
        <Menu className="dorm-list" onSelect={onSelect} activeItemId={activeItem}>
            <MenuContent>
                {/*група*/}
                <MenuGroup>
                    {/*список елементів групи*/}
                    <MenuList>
                        {/*елемент групи із посиланням на дію\сторінку\тощо*/}
                        <MenuItem to="#" itemId={0}>
                            Central
                        </MenuItem>
                    </MenuList>
                </MenuGroup>
                {/*гарна полосочка*/}
                <Divider />
                {floors.map((floor, idx) =>
                    <MenuGroup label={floor.name} labelHeadingLevel="h3">
                        <MenuList>
                            {floor?.switches.map(switchItem =>
                                <DormSwitch {...switchItem} />)}
                        </MenuList>
                    </MenuGroup>
                )}
            </MenuContent>
        </Menu>
    );
};
