import React, {useEffect, useRef, useState} from "react";
import './Terminal.css';
import {ISwitch} from "../../core/models";
import {useParams} from "react-router-dom";
import {switchesMock} from "../../core/mocks/dormMock";
export const Terminal = () => {
    const shellinaboxFrameRef = useRef(null);
    const [switchItem, setSwitchItem] = useState<ISwitch | undefined>();
    const { switchId } = useParams();
    useEffect(() => {
        if (switchId) {
            const foundedSwitch = switchesMock[switchId];
            setSwitchItem(foundedSwitch);
            // Send the message to the iframe containing the Shell In A Box instance
            const message = sendInput(foundedSwitch.commands);
            shellinaboxFrameRef.current && shellinaboxFrameRef?.current.contentWindow.postMessage(message, '*');
        }
    }, [switchId])
    console.log(switchItem);

    // Send input to the Shell In A Box instancontentWindowce
    const sendInput = (commands: string[]) => {
        // Create a message object containing the input string
        return {
            type: 'input',
            data: commands.reduce((total, command) => total + `<br/> ${command}`)
        };
    }

    return <div className="terminal-wrapper">
        {/* eslint-disable-next-line jsx-a11y/iframe-has-title */}
        <iframe ref={shellinaboxFrameRef} className="terminal" src="http://localhost:43333/" width="100%"></iframe>
    </div>
}
